import React, { Component } from 'react'

export  default class  extends Component {
    constructor(props) {
        super(props)

        this.state = {
             count : 0
        }
    }

    IncrementCounter = () =>{
        this.setState(this.state.count+1)
    }

    render() {
       const  {count} = this.state;
        return (
            <div>
                <button onMouseEnter ={this.IncrementCounter}>Increment on hover</button>
                <h1>{count}</h1>
            </div>
        )
    }
}
