import React, { Component } from 'react'

export default class Classcomp extends Component {

    constructor(props) {
        super(props)
        this.state = {
             count : 0
        }
    }

    render() {
    const count=this.state
       const Increment = () =>{
            count = count +1
        }   

        return (
            <div>
                <button onClick={Increment}>clicked {count}</button>
            </div>
        )
    }
}
